SELECT company_code, founder, COUNT(DISTINCT(lead_manager_code)), COUNT(DISTINCT(senior_manager_code)), COUNT(DISTINCT(manager_code)), COUNT(DISTINCT(employee_code)) FROM company LEFT OUTER JOIN lead_manager USING(company_code) LEFT OUTER JOIN senior_manager USING(lead_manager_code, company_code) LEFT OUTER JOIN manager USING(senior_manager_code, lead_manager_code, company_code) LEFT OUTER JOIN employee USING(manager_code, senior_manager_code, lead_manager_code, company_code) GROUP BY company_code, founder ORDER BY company_code;